import React, { Component } from 'react';
import ItemList from '../../components/ItemList';

const headers = [
    {
      key: "word",
      header: "word",
    },
    {
      key: "score",
      header: "score",
    }
  ];

var rowsToBeDisplayed = []

class WordDisplay extends React.Component {

    getWordList (){
        console.log("getting synonyms for word "+this.props)
        fetch('https://api.datamuse.com/words?ml='+this.props.word)
        .then(res => res.json())
        .then((data) => {
          var rows = data.map((dataPoint, index)=>({
            id:index,
            word:dataPoint.word,
            score:dataPoint.score,
          }));
          rowsToBeDisplayed = rows
        })
        .catch(console.log)
    }


    render (){
        getWordList()
        return (
            <> 
            <h3>This is the word display element for the word:<br/> {this.props.word}</h3>
            <ItemList
                headers = {headers}
                rows = {rowsToBeDisplayed}
            />

            </>
        );
    }
}
export default WordDisplay;
