import React from 'react';
import WordDisplay from './SynonimDisplay';
import Button from "../../../node_modules/carbon-components-react/lib/components/Button";



class SynonymPage extends React.Component {
  state = {
    word : "banana tree"
  }
  updateWord (newWord){
    this.setState({word:newWord})
  }

  render (){
    return (
      <div>
        <Button
          onClick = {function (){
            this.setState (updateWord("Duck"))
          }}
        >
          Duck
        </Button>
        <WordDisplay word = {this.state.word}/>
      </div>
    );
  }
};
export default SynonymPage;
