import React, { Component } from 'react';

class WordDisplay extends React.Component {
    state = {
        synonims: []
    }

    componentDidMount() {
        fetch('https://api.datamuse.com/words?ml='+this.props.word)
        .then(res => res.json())
        .then((data) => {
          this.setState({ synonims: data })
          console.log(data)
        })
        .catch(console.log)
    }



    render (){
        return (
            <> This is the word display element for the word {this.props.word} </>

        );
    }
}
export default WordDisplay;
