import React, { Component } from 'react';
import ItemList from '../../components/ItemList';

const headers = [
    {
      key: "word",
      header: "word",
    },
    {
      key: "score",
      header: "score",
    }
  ];



class WordDisplay extends React.Component {
    state = {
        word: this.props.word,
        synonims: []
    }


    getWordList (word){
        this.setState ({word:"", synonims:[]})
        fetch('https://api.datamuse.com/words?ml='+word)
        .then(res => res.json())
        .then((data) => {
          var rows = data.map((dataPoint, index)=>({
            id:index,
            word:dataPoint.word,
            score:dataPoint.score,
          }));
          this.setState({synonims:rows})
        })
        .catch(console.log)
        return this.state.synonims;
    }


    render (){
        return (
            <> 
            <h3>This is the word display element for the word:<br/> {this.props.word}</h3>
            <ItemList
                headers = {headers}
                rows = {this.getWordList(this.props.word)}
            />

            </>
        );
    }
}
export default WordDisplay;
