import React, { Component } from 'react';
import ItemList from '../../components/ItemList';

const headers = [
    {
      key: "word",
      header: "word",
    },
    {
      key: "score",
      header: "score",
    }
  ];


class WordDisplay extends React.Component {
    state = {
        word: this.props.word,
        rows :  []
    }

    getWordList (){
        console.log("getting synonyms for word "+this.props.word)
        fetch('https://api.datamuse.com/words?ml='+this.props.word)
        .then(res => res.json())
        .then((data) => {
            this.setState ({
                word : this.props.word,
                rows : data.map((dataPoint, index)=>({
                    id:index,
                    word:dataPoint.word,
                    score:dataPoint.score,
                }))
            });
        })
        .catch(console.log)
    }


    render (){
        return (
            <> 
            <h3>This is the word display element for the word:<br/> {this.state.word}</h3>
            <ItemList
                headers = {headers}
                rows = {this.state.rows}
            />

            </>
        );
    }
}
export default WordDisplay;
