import React, { Component } from 'react';
import ItemList from '../../components/ItemList';

const headers = [
    {
      key: "word",
      header: "Word",
    },
    {
      key: "score",
      header: "Score (/100000)",
    }
  ];


class WordDisplay extends React.Component {
    state = {
        word: this.props.word,
        rows :  [],
        version : 0
    }

    getWordList (){
        if (this.props.word===this.state.word) return;// this should not be necessary
        console.log("getting synonyms for word "+this.props.word)
        fetch('https://api.datamuse.com/words?ml='+this.props.word)
        .then(res => res.json())
        .then((data) => {
            this.setState ({
                word : this.props.word,
                rows : data.map((dataPoint, index)=>({
                    id:index,
                    word:dataPoint.word,
                    score:dataPoint.score,
                })),
                version : this.state.version + 1
            });
        }).then(()=>{
            return 
        })
        .catch(console.log)
    }

    shouldComponentUpdate(nextProps, nextState) { 
        if (nextState.version !== this.state.version) { 
          return false;
        }
        return true;
      }

    render (){
        this.getWordList();
        return (
            <> 
                <h3>Showing synonyms for {this.state.word}</h3>
                <ItemList
                    headers = {headers}
                    rows = {this.state.rows}
                />
            </>
        );
    }
}
export default WordDisplay;
