import React, { Component } from 'react';
import ItemList from '../../components/ItemList';

const headers = [
    {
      key: "word",
      header: "word",
    },
    {
      key: "score",
      header: "score",
    }
  ];

rowsToBeDisplayed = []

class WordDisplay extends React.Component {
    updateWordList (){
        console.log("getting synonyms for word "+this.props.word)
        fetch('https://api.datamuse.com/words?ml='+this.props.word)
        .then(res => res.json())
        .then((data) => {
            rowsToBeDisplayed = data.map((dataPoint, index)=>({
                id:index,
                word:dataPoint.word,
                score:dataPoint.score,
            }));
            })
        .catch(console.log)
    }


    render (){
        this.updateWordList()
        return (
            <> 
            <h3>This is the word display element for the word:<br/> {this.props.word}</h3>
            <ItemList
                headers = {headers}
                rows = {rowsToBeDisplayed}
            />

            </>
        );
    }
}
export default WordDisplay;
